INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('1', 'به بوی نافه‌ای کاخر صبا زان طره بگشاید', 'ز تاب جعد مشکینش چه خون افتاد در دل‌ها', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('2', 'دلم ز صومعه بگرفت و خرقه سالوس', 'کجاست دیر مغان و شراب ناب کجا', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('3', 'اگر دشنام فرمایی و گر نفرین دعا گویم', 'جواب تلخ می‌زیبد لب لعل شکرخا را', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('4', 'صبا به لطف بگو آن غزال رعنا را', 'که سر به کوه و بیابان تو داده‌ای ما را', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('5', 'در کوی نیک نامی ما را گذر ندادند', 'گر تو نمی‌پسندی تغییر کن قضا را', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('6', 'به ملازمان سلطان که رساند این دعا را', 'که به شکر پادشاهی ز نظر مران گدا را', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('7', 'صوفی بیا که آینه صافیست جام را', 'تا بنگری صفای می لعل فام را', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('8', 'ساقیا برخیز و درده جام را', 'خاک بر سر کن غم ایام را', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9', 'هر که را خوابگه آخر مشتی خاک است', 'گو چه حاجت که به افلاک کشی ایوان را', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('10', 'ساقی به نور باده برافروز جام ما', 'مطرب بگو که کار جهان شد به کام ما', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('11', 'دل خرابی می‌کند دلدار را آگه کنید', 'زینهار ای دوستان جان من و جان شما', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('12', 'تیری که زدی بر دلم از غمزه خطا رفت', 'تا باز چه اندیشه کند رای صوابت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('13', 'کنون به آب می لعل خرقه می‌شویم', 'نصیبه ازل از خود نمی‌توان انداخت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('14', 'سینه از آتش دل در غم جانانه بسوخت', 'آتشی بود در این خانه که کاشانه بسوخت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('15', 'هر که آمد به جهان نقش خرابی دارد', 'در خرابات بگویید که هشیار کجاست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('16', 'مست بگذشتی و از خلوتیان ملکوت', 'به تماشای تو آشوب قیامت برخاست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('17', 'از آن به دیر مغانم عزیز می‌دارند', 'که آتشی که نمیرد همیشه در دل ماست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('18', ' خیال روی تو در هر طریق همره ماست', 'نسیم موی تو پیوند جان آگه ماست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('19', 'برو ای زاهد و بر دردکشان خرده مگیر', 'که ندادند جز این تحفه به ما روز الست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('20', 'ما را ز خیال تو چه پروای شراب است', 'خم گو سر خود گیر که خمخانه خراب است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('21', 'مطرب چه پرده ساخت که در پرده سماع', 'بر اهل وجد و حال در های و هو ببست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('22', 'تو خود وصال دگر بودی ای نسیم وصال', 'خطا نگر که دل امید در وفای تو بست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('23', 'ای مدعی برو که مرا با تو کار نیست', 'احباب حاضرند به اعدا چه حاجت است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('24', 'رواق منظر چشم من آشیانه توست', 'کرم نما و فرود آ که خانه خانه توست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('25', 'گدای کوی تو از هشت خلد مستغنیست', 'اسیر عشق تو از هر دو عالم آزادست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('26', 'دل من در هوس روی تو ای مونس جان', 'خاک راهیست که در دست نسیم افتادست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('27', 'بیا که قصر امل سخت سست بنیادست', 'بیار باده که بنیاد عمر بر بادست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('28', 'بی مهر رخت روز مرا نور نماندست', 'وز عمر مرا جز شب دیجور نماندست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('29', 'باغ مرا چه حاجت سرو و صنوبر است', 'شمشاد خانه پرور ما از که کمتر است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('30', 'صراحی ای و حریفی گرت به چنگ افتد', 'به عقل نوش که ایام فتنه انگیز است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('31', 'حال دل با تو گفتنم هوس است', 'خبر دل شنفتنم هوس است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('32', 'کنون که بر کف گل جام باده صاف است', 'به صد هزار زبان بلبلش در اوصاف است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('33', 'به چشم عقل در این رهگذار پرآشوب', 'جهان و کار جهان بی‌ثبات و بی‌ محل است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('34', 'گل در بر و می در کف و معشوق به کام است', 'سلطان جهانم به چنین روز غلام است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('35', 'به کوی میکده هر سالکی که ره دانست', 'دری دگر زدن اندیشه تبه دانست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('36', 'ای که از دفتر عقل آیت عشق آموزی', 'ترسم این نکته به تحقیق ندانی دانست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('37', 'گنج عزلت که طلسمات عجایب دارد', 'فتح آن در نظر رحمت درویشان است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('38', 'مرو به خانه ارباب بی‌مروت دهر', 'که گنج عافیتت در سرای خویشتن است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('39', 'بنده طالع خویشم که در این قحط وفا', 'عشق آن لولی سرمست خریدار من است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('40', 'روزگاریست که سودای بتان دین من است', 'غم این کار نشاط دل غمگین من است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('41', 'منم که گوشه میخانه خانقاه من است', 'دعای پیر مغان ورد صبحگاه من است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('42', 'ز گریه مردم چشمم نشسته در خون است', 'ببین که در طلبت حال مردمان چون است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('43', 'خم زلف تو دام کفر و دین است', 'ز کارستان او یک شمه این است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('44', 'دل سراپرده محبت اوست', 'دیده آیینه دار طلعت اوست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('45', 'آن سیه چرده که شیرینی عالم با اوست', 'چشم میگون لب خندان دل خرم با اوست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('46', 'صبا ز حال دل تنگ ما چه شرح دهد', 'که چون شکنج ورق‌های غنچه تو بر توست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('47', 'دارم امید عاطفتی از جانب دوست', 'کردم جنایتی و امیدم به عفو اوست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('48', 'آن پیک نامور که رسید از دیار دوست', 'آورد حرز جان ز خط مشکبار دوست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('49', 'صبا اگر گذری افتدت به کشور دوست', 'بیار نفحه‌ای از گیسوی معنبر دوست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('50', 'مرحبا ای پیک مشتاقان بده پیغام دوست', 'تا کنم جان از سر رغبت فدای نام دوست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('51', 'روی تو کس ندید و هزارت رقیب هست', 'در غنچه‌ای هنوز و صدت عندلیب هست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('52', 'اگر چه عرض هنر پیش یار بی‌ادبیست', 'زبان خموش ولیکن دهان پر از عربیست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('53', 'بنال بلبل اگر با منت سر یاریست', 'که ما دو عاشق زاریم و کار ما زاریست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('54', 'یا رب این شمع دل افروز ز کاشانه کیست', 'جان ما سوخت بپرسید که جانانه کیست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('55', 'زاهد ظاهرپرست از حال ما آگاه نیست', 'در حق ما هر چه گوید جای هیچ اکراه نیست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('56', 'راهیست راه عشق که هیچش کناره نیست', 'آن جا جز آن که جان بسپارند چاره نیست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('57', 'روشن از پرتو رویت نظری نیست که نیست', 'منت خاک درت بر بصری نیست که نیست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('58', 'حاصل کارگه کون و مکان این همه نیست', 'باده پیش آر که اسباب جهان این همه نیست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('59', 'خواب آن نرگس فتان تو بی چیزی نیست', 'تاب آن زلف پریشان تو بی چیزی نیست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('60', 'جز آستان توام در جهان پناهی نیست', 'سر مرا بجز این در حواله گاهی نیست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('61', 'بلبلی برگ گلی خوش رنگ در منقار داشت', 'و اندر آن برگ و نوا خوش ناله‌های زار داشت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('62', 'الا یا ایها الساقی ادر کاسا و ناولها', 'که عشق آسان نمود اول ولی افتاد مشکل‌ها', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('63', 'کنون که می‌دمد از بوستان نسیم بهشت', 'من و شراب فرح بخش و یار حورسرشت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('64', 'عیب رندان مکن ای زاهد پاکیزه سرشت', 'که گناه دگران بر تو نخواهند نوشت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('65', 'صبحدم مرغ چمن با گل نوخاسته گفت', 'ناز کم کن که در این باغ بسی چون تو شکفت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('66', 'آن ترک پری چهره که دوش از بر ما رفت', 'آیا چه خطا دید که از راه خطا رفت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('67', 'ساقی بیار باده که ماه صیام رفت', 'در ده قدح که موسم ناموس و نام رفت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('68', 'شربتی از لب لعلش نچشیدیم و برفت', 'روی مه پیکر او سیر ندیدیم و برفت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('69', 'ساقی بیا که یار ز رخ پرده برگرفت', 'کار چراغ خلوتیان باز درگرفت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('70', 'ای هدهد صبا به سبا می‌فرستمت', 'بنگر که از کجا به کجا می‌فرستمت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('71', 'ای غایب از نظر به خدا می‌سپارمت', 'جانم بسوختی و به دل دوست دارمت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('72', 'چه لطف بود که ناگاه رشحه قلمت', 'حقوق خدمت ما عرضه کرد بر کرمت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('73', 'زان یار دلنوازم شکریست با شکایت', 'گر نکته دان عشقی بشنو تو این حکایت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('74', 'تویی که بر سر خوبان کشوری چون تاج', 'سزد اگر همه دلبران دهندت باج', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('75', 'دعای جان تو ورد زبان مشتاقان', 'همیشه تا که بود متصل مسا و صباح', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('76', 'دل من در هوای روی فرخ', 'بود آشفته همچون موی فرخ', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('77', 'دی پیر می فروش که ذکرش به خیر باد', 'گفتا شراب نوش و غم دل ببر ز یاد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('78', 'دوش آگهی ز یار سفرکرده داد باد', 'من نیز دل به باد دهم هر چه باد باد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('79', 'روز وصل دوستداران یاد باد', 'یاد باد آن روزگاران یاد باد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('80', 'صوفی ار باده به اندازه خورد نوشش باد', 'ور نه اندیشه این کار فراموشش باد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('81', 'تنت به ناز طبیبان نیازمند مباد', 'وجود نازکت آزرده گزند مباد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('82', 'حسن تو همیشه در فزون باد', 'رویت همه ساله لاله گون باد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('83', 'خسروا گوی فلک در خم چوگان تو باد', 'ساحت کون و مکان عرصه میدان تو باد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('84', 'دیری است که دلدار پیامی نفرستاد', 'ننوشت سلامی و کلامی نفرستاد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('85', 'پیرانه سرم عشق جوانی به سر افتاد', 'وان راز که در دل بنهفتم به درافتاد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('86', 'ای دل اندربند زلفش از پریشانی منال', 'مرغ زیرک چون به دام افتد تحمل بایدش', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('87', 'دلربایی همه آن نیست که عاشق بکشند', 'خواجه آن است که باشد غم خدمتگارش', 'حافظ', '0');
