INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('90', 'ببرد از من قرار و طاقت و هوش', 'بت سنگین دل سیمین بناگوش', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('91', 'صوفی ز کنج صومعه با پای خم نشست', 'تا دید محتسب که سبو می‌کشد به دوش', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('92', 'دوش با من گفت پنهان کاردانی تیزهوش', 'و از شما پنهان نشاید کرد سر می فروش', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('93', 'در ره عشق که از سیل بلا نیست گذار', 'کرده‌ام خاطر خود را به تمنای تو خوش', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('94', 'یار دلدار من ار قلب بدین سان شکند', 'ببرد زود به جانداری خود پادشهش', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('95', 'دلم رمیده شد و غافلم من درویش', 'که آن شکاری سرگشته را چه آمد پیش', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('96', 'ما آزموده‌ایم در این شهر بخت خویش', 'بیرون کشید باید از این ورطه رخت خویش', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('97', 'در شب هجران مرا پروانه وصلی فرست', 'ور نه از دردت جهانی را بسوزانم چو شمع', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('98', 'گویی بدهم کامت و جانت بستانم', 'ترسم ندهی کامم و جانم بستانی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('99', 'بگو که جان عزیزم ز دست رفت خدا را', 'ز لعل روح فزایش ببخش آن که تو دانی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('100', 'دو یار زیرک و از باده کهن دومنی', 'فراغتی و کتابی و گوشه چمنی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('101', 'نوش کن جام شراب یک منی', 'تا بدان بیخ غم از دل برکنی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('102', 'خون پیاله خور که حلال است خون او', 'در کار یار باش که کاریست کردنی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('103', 'ای که در کشتن ما هیچ مدارا نکنی', 'سود و سرمایه بسوزی و محابا نکنی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('104', 'آخرالامر گل کوزه گران خواهی شد', 'حالیا فکر سبو کن که پر از باده کنی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('105', 'ای دل به کوی عشق گذاری نمی‌کنی', 'اسباب جمع داری و کاری نمی‌کنی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('106', 'نازنینی چو تو پاکیزه دل و پاک نهاد', 'بهتر آن است که با مردم بد ننشینی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('107', 'ساقیا سایه ابر است و بهار و لب جوی', 'من نگویم چه کن ار اهل دلی خود تو بگوی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('108', 'در مکتب حقایق پیش ادیب عشق', 'هان ای پسر بکوش که روزی پدر شوی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('109', 'سحرم هاتف میخانه به دولت خواهی', 'گفت بازآی که دیرینه این درگاهی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('110', 'ساقی بیار آبی از چشمه خرابات', 'تا خرقه‌ها بشوییم از عجب خانقاهی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('111', 'دل که آیینه شاهیست غباری دارد', 'از خدا می‌طلبم صحبت روشن رایی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('112', 'مکدر است دل آتش به خرقه خواهم زد', 'بیا ببین که که را می‌کند تماشایی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('113', 'سلامی چو بوی خوش آشنایی', 'بدان مردم دیده روشنایی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('114', 'دایم گل این بستان شاداب نمی‌ماند', 'دریاب ضعیفان را در وقت توانایی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('116', 'همت بلند دار که نزد خدا و خلق', 'باشد به قدر همت تو، اعتبار تو', 'ابن یمین', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('121', 'گر با دگران به از منی، وای به من', 'ور با همه کس همچو منی، وای همه', 'ابوسعید ابوالخیر', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('123', 'عشق، شیریست قوی پنجه و می گوید فاش', 'هر که از جان گذرد بگذرد از بیشه ی ما', 'ادیب نیشابوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('124', 'خدا مرا به فراق تو مبتلا نکند', 'نصیب دشمن ما را نصیب ما نکند', 'ادیب نیشابوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('125', 'ز آدمی به جهان نام نیک ماند و بس', 'به مهر کوش که گیتی به کس وفا نکند', 'ادیب نیشابوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('127', 'رفیقی بایدم همدم، به شادی یار و در غم هم', 'وزین خویشان نامحرم مرا بیگانگی باید', 'اقبال لاهوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('128', 'خدا آن ملتی را سروری داد', 'که تقدیرش بدست خویش بنوشت', 'اقبال لاهوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('129', 'در جهان بال و پر خویش گشودن آموز', 'كه پریدن نتوان با پر و بال دگران', 'اقبال لاهوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('131', 'سر گشته چو پرگار همه عمر دویدیم', 'آخر به همان نقطه كه بودیم رسیدیم', 'امام فخر رازی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('134', 'ز لب دوختن غنچه را زندگی ست', 'چو بشکفت زان پس پراکندگی است', 'امیر خسرو دهلوی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('135', 'می روی و گریه می آید مرا', 'ساعتی بنشین که باران بگذرد', 'امیر خسرو دهلوی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('136', 'راه مردان به خود فروشی نیست', 'در جهان بهتر از خموشی نیست', 'اوحدی مراغه ای', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('137', 'کم شود مهر ز دوری دگران را لیکن', 'کم نشود مهر من از دوری و افزود بیا', 'اوحدی مراغه ای', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('138', 'شعر نوری ز عرش زاینده است', 'زان چو عرش استوار و پاینده است', 'اوحدی مراغه ای', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('139', 'هر که غیرت نداشت دینش نیست', 'آن ندارد کسی که اینش نیست', 'اوحدی مراغه ای', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('140', 'همی وعده دهی امروز و فردا', 'همین امروز و فردایت مرا کشت', 'بابا طاهر عریان', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('141', 'به صحرا بنگرم صحرا تو بینم ', 'به دریا بنگرم دریا تو بینم', 'بابا طاهر عریان', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('145', 'افسوس كه افسانه سرایان همه خفتند ', 'اندوه كه اندوه گساران همه رفتند', 'ملک الشعرای بهار', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('149', 'عاشقی مقدور هر عیاش نیست', 'غم کشیدن صنعت نقاش نیست', 'بیدل دهلوی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('150', 'از خوان این بزرگان دستی بشوی و بگذر', 'کان جا ز خوردنی ها غیر از قسم نباشد', 'بیدل دهلوی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('151', 'پیوستگی به حق ز دو عالم بریدن است', 'دیدار دوست، هستی خود را ندیدن است', 'بیدل دهلوی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('152', 'هر کجا رفتیم داغی بر دل ما تازه شد', 'سوخت آخر جنس ما از گرمی بازارها', 'بیدل دهلوی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('154', 'پدر، آن تیشه که بر خاک تو زد دست اجل', 'شیشه ای بود که شد باعث ویرانی من', 'پروین اعتصامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('155', 'حدیث نیک و بد ما نوشته خواهد شد', 'زمانه را سند و دفتری و دیوانی است', 'پروین اعتصامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('156', 'قناعت کن اگر در آرزوی گنج قارونی', 'گدای خویش باش ار طالب ملک سلیمانی', 'پروین اعتصامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('157', 'ما نمی پوشیم عیب خویش، اما دیگران', 'عیب ها دارند و از ما جمله را پوشیده اند', 'پروین اعتصامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('158', 'ای دوست دزد حاجب و دربان نمیشود ', 'گرگ سیه درون، سگ چوپان نمیشود', 'پروین اعتصامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('159', 'دام تزویر كه گستردیم بهر صید خلق', 'كرد مارا پایبند و خود شدیم آخر شكار', 'پروین اعتصامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('160', 'زندگی جز نفسی نیست غنیمت شمرش', 'نیست امید که همواره نفس برگردد', 'پروین اعتصامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('161', 'همیشه دختر امروز، مادر فرداست', 'ز مادر است میُسّر بزرگی پسران', 'پروین اعتصامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('163', 'یک دل و یک جهت و یک رو باش', 'از دو رویان جهان یک سو باش ', 'جامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('164', 'ای فلك اندوه شیرین بر دل خسرو منه', 'كاین بضاعت را خریداری به از فرهاد نیست', 'جامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('165', 'گفتن بسیار نه از نَغزی است', 'ولوله ی طبل ز بی مغزی است', 'جامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('168', 'اگر آن ترک شیرازی بدست آرد دل مارا', 'به خال هندویش بخشم سمرقند و بخارا را', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('169', 'اگر به زلف دراز تو دست ما نرسد ', 'گناه بخت پریشان و دست كوته ماست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('170', 'الا ای پیر فرزانه مکن منعم ز میخانه', 'که من در ترک پیمانه دلی پیمان شکن دارم', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('171', 'ای پادشه خوبان داد از غم تنهای', ' دل بی تو به جان آمد وقت است که باز آیی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('172', 'ای دل مباش یکدم خالی ز عشق و مستی', 'وانگه برو که رستی از نیستی و هستی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('173', 'ای که دایم به خویش مغروری', ' گر ترا عشق نیست معذوری', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('174', 'ای دوست به كام دشمنانم كردی', ' بودم چو بهار چون خزانم كردی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('175', 'ای غایب از نظر به خدا می سپارمت ', 'جانم بسوختی و به دل دوست دارمت', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('176', 'ای نسیم سحر آرامگه یار كجاست ', 'منزل آن مه عاشق كش عیار كجاست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('177', 'آتش عشق پس از مرگ نگردد خاموش', 'این چراغی است کزین خانه به آن خانه برند', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('178', 'آسایش دو گیتی تفسیر این دو حرف است ', 'با دوستان مروت با دشمنان مدارا', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('179', 'بر آستان جانان گر سر توان نهادن', 'گلبانگ سربلندی بر آسمان توان زد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('180', 'ترا که هر چه مراد است در جهان داری', 'چه غم ز حال ضعیفان ناتوان داری', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('181', 'تو همچو صبحی من شمع خلوت سحرم', 'تبسمی کن و جان بین که همی سپرم', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('182', 'ثواب روزه و حج قبول آن کس برد', 'که خاک میکده ی عشق را زیارت کرد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('183', 'حدیث دوست نگویم مگر به حضرت دوست', 'که آشنا سخن آشنا نگه دارد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('184', 'در این بازار گر سودیست با درویش خرسندست', 'خدایا منعمم گردان به درویشی و خرسندی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('185', 'در دایره قسمت ما نقطه ی تسلیمیم', 'لطف آنچه تو اندیشی حكم آنچه تو فرمایی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('186', 'در دلم بود كه بی دوست نباشم هرگز', 'چه توان كرد كه سعی من و دل باطل بود', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('187', 'دیدی ای دل كه غم عشق دگر بار چه كرد', 'چون بشد دلبر و با یار وفادار چه كرد', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('188', 'رازی که بر غیر نگفتیم و نگوییم', 'با دوست بگوییم که او محرم راز است', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('189', 'زلف او دام است و خالش دانه ی آن دام و من', 'بر امید دانه ای افتاده ام در دام دوست', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('190', 'ساقی و مطرب و می جمله مهیاست ولی', 'عیش بی یار مهیا نشود یار کجاست؟', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('191', 'ساقیا آمدن عید مبارک بادت', 'وان مواعید که کردی مرود از یادت ', 'حافظ', '0');
