INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('359', 'پرواز من به بال و پر توست، زینهار', ' مشکن مرا که میشکنی بال خویش را', 'صائب شیرازی', '0');
