INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('404', 'کسی کو فروتن تر و رادتر', 'دل دوستانش از او شادتر', 'فردوسی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('405', 'هر آنگه که موی سیه شد سپید', 'به بودن نماند فراوان امید', 'فردوسی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('406', 'جانی که خلاص از شب هجران تو کردم', 'در روز وصال تو به قربان تو کردم', 'فروغ بسطامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('410', 'امشب تو را بخوبی نسبت به ماه کردم', 'تو خوبتر ز ماهی، من اشتباه کردم', 'فروغی بسطامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('411', 'عقل پرسید که دشوارتر از مردن چیست', 'عشق فرمود فراق از همه دشوارتر است', 'فروغی بسطامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('412', 'غیبت نکرده ای که شوم طالب حضور', 'پنهان نگشته ای که هویدا کنم تو را', 'فروغی بسطامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('413', 'یا گل نورسته شو، یا بلبل شوریده باش', 'یا چراغ خانه، یا آتش به جان پروانه باش', 'فروغی بسطامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('414', 'یک جمع نکوشیده رسیدند به مقصد', 'یک قوم دویدند و به مقصد نرسیدند', 'فروغی بسطامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('415', 'بار محبت از همه باری گرانتر است', 'وان کس کشد که از همه کس ناتوانتر است', 'فروغی بسطامی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('416', 'تو عهد كرده ای كه كشانی به خون مرا', 'من جهد كرده ام كه به عهدت وفا كنی', 'فروغی بسطامی ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('419', 'آخر به اسارت دل حسرت زده خو کرد', 'شادم که دگر یاد گریز از قفسم نیست', 'فریدون توللی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('420', 'زندگی گرمی دل های به هم پیوسته ست', 'تا در آن دوست نباشد همه درها بسته ست', 'فریدون مشیری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('421', 'آدمیزاد اگر بی ادب است آدم نیست ', 'فرق در بین بنی آدم و حیوان ادب است', 'فصاحت رازی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('422', 'از گریه سوختیمو تو آهی نمی کنی', ' در آب و آتشیم و نگاهی نمی کنی', 'فغانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('423', 'روزگار است این که گه عزت دهد گه خوار دارد', 'چرخ بازیگر از این بازیچه ها بسیار دارد', 'قائم مقام فراهانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('426', 'چشم دلجویی دلم از مردم عالم نداشت', 'داغ من مرهم ندید و راز من محرم نداشت', 'کلیم کاشانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('427', 'طبعی به هم رسان که بسازی عالمی', 'یا همتی که از سر عالم توان گذشت', 'کلیم کاشانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('429', 'تا به فراق خو كنم صبر من و قرار كو؟', 'وعدۀ وصل اگر دهد طاقت انتظار كو؟', 'كلجاری ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('430', 'جز کوی توام نیست به سر فکر مقامی', 'تا عمر به پایان برسد منزلم این است', 'گلشن کردستانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('431', 'از عشق فقط وصل و رسیدن خوش نیست', 'عشق است و همین لذت دیدار و دگر هیچ', 'لاادری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('432', 'یک جهان بر هم زدم وز جمله بگزیدم تو را', 'من چه می کردم به عالم گر نمی دیدم تو را ', 'لاهیجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('433', 'مغرور مشو این همه بر سوز خود ای شمع', 'کاین سازش پروانه هم از روی حساب است', 'لنگرودی', '0');
