INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('444', 'سال ها رفت و زیادم نرود دوست هنوز', 'تا چه کردم که مرا دشمن جان اوست هنوز', 'مشفق کاشانی', '0');
