INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('446', 'شایان تماشای تو دل باختن است', 'بی خویش به دیدار تو پرداختن است', 'مشفق کاشانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('447', 'هر نا کس و کس می کند آزار دل من', 'با آنکه به گیتی سر آزار کسم نیست', 'مشفق کاشانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('448', 'دانی ز چه غنچه خون كندچهره ز شرم؟', 'زان روی كه كار او گل انداختن است', 'مشفق كاشانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('449', 'صبر بر درد، نه از همت مردانه ی ماست', 'درد از او، صبر از او، همت مردانه از اوست ', 'مصاحبی نائینی', '0');
