INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('464', 'اندر دل بی وفا غم و ماتم باد', 'آن را که وفا نیست ز عالم کم باد', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('465', 'ای قوم به حج رفته کجایید کجایید', 'معشوق همین جاست بیایید بیایید', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('466', 'دیدی که مرا هیچ کسی یاد نکرد', 'جز غم که هزار آفرین بر غم باد', 'مولانا', '0');
