INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('468', 'شکر نعمت نعمتت افزون کند', 'کفر نعمت از کفت بیرون کند', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('469', 'ما چو ناییم و نوا در ما ز توست', 'ما چو کوهیم و صدا در ما ز توست', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('470', 'اگر از جانب معشوق نباشد کششی', 'کوشش عاشق بیچاره بجایی نرسد', 'مولوی', '0');
