INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('475', 'کدام دانه فرو رفت در زمین که نرست', 'چرا به دانه ی انسانیت این گمان باشد', 'مولوی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('476', 'ما را نه غم دوزخ و نه حرص بهشت است', 'بردار پرده ز رخ که مشتاق لقاییم', 'مولوی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('477', 'ما ز بالاییم و بالا می رویم', 'ما ز دریاییم و دریا می رویم', 'مولوی', '0');
