INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('485', 'تا نگرید طفلك حلوا فروش', ' دیگ بخشایش كجا آید به جوش', 'مولوی ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('493', 'جز دل که گیرد جان من جز من که گیرد جان دل', 'گر دل بمیرد وای من گر منبمیرم وای دل', 'مهرداد اوستا', '0');
