INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('497', 'منم و دلی که دائم به دو دست دارم او را', 'اگرش نگاه داری به تو می سپارم او را', 'میر صبری اصفهانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('499', 'عاشقی را شرط تنها ناله و فریاد نیست', 'تا کسی از جان شیرین نگذرد فرهاد نیست', 'میرزاده عشقی', '0');
