INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('501', 'با ما کج و با خود کج و با خلق خدا کج', 'آخر قدمی راست بنه ای همه جا کج', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('502', 'جز راست مگوی گاه و بی گاه', 'تا حاجت نایدت به سوگند', 'ناصر خسرو', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('503', 'چون خود نکنی چنان که گویی', 'پند تو بود دروغ و ترفند', 'ناصر خسرو', '0');
