INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('506', 'جوانی بر سر کوچ است، دریاب این جوانی را', ' که شهری باز، نشناسد غریب کاروانی را', 'نظامی', '0');
