INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('511', 'کسی کز عشق خالی شد، فرسوده است', 'گرش صد جان بود بی عشق مرده است', 'نظامی', '0');
