INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('519', 'جمع ما جمع نباشد تو پریشان باشی', 'یاد حیرانی ما آری و حیران باشی', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('520', 'چو دیدم خوار خود را از در آن بی وفا رفتم', 'رسد روزی که قدر من بداند حالیا رفتم', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('521', 'روم به جای دگر دل دهم به یار دگر', 'هوای یار دگر دارم و دیار دگر', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('522', 'یک وعده خواهم از تو که باشم در انتظار', 'حاکم تویی در آمدن دیر و زود خویش', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('523', 'ای گل تازه كه بویی زوفا نیست تو را ', 'خبر از سرزنش خار جفا نیست تو را؟', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('524', 'در این فكرم كه خواهی ماند با من مهربان یا نه؟', 'به من كم می كنی لطفی كه داری این زمان یا نه؟', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('525', 'شرح این آتش جانسوز نگفتن تا کی؟', 'سوختم، سوختم این راز نهفتن تا کی؟', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('526', 'گر بدانی حال من گریان شوی بی اختیار', 'ای که منع گریه بی اختیارم می کنی', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('527', 'گر چه دوری می کنم بی صبر و آرامم هنوز', 'می نمایم اینچنین وحشی ولی رامم هنوز', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('528', 'می یابم از خود حسرتی باز از فراق کیست این', 'آماده ی صد گریه ام از اشتیاق کیست این', 'وحشی بافقی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('529', 'پروانه به یک سوختن آزاد شد از شمع ', 'بیچاره دل ماست که در سوز و گدازست', 'وصال شیرازی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('530', 'طبیب شهر که هر درد را دوایی گفت', 'به درد عشق نداند کسی چه درمان گفت', 'وصال شیرازی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('531', 'اول اندر كوی او جز نقش پای ما نبود', 'آخر آنجا از هجوم خلق، جای ما نبود', 'وصال شیرازی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('532', 'قضا رفت و قلم بنوشت فرمان', ' تو را جز صبر کردن چیست درمان', 'فخر الدین اسعد گرگانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('533', 'همه جا به بی وفایی مثلند خوبرویان', 'تو میان خوبرویان مثلی به بی وفایی', 'هاتف اصفهانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('537', 'جان خوشست اما نمی خواهم که جان گویم تو را', 'خواهم از جان خوشتری یابم که آن گویم تو را', 'هلالی جغتایی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('538', 'هر شبی گویم که فردا ترک این سودا کنم', 'باز چون فردا شود امروز را فردا کنم', 'هلالی جغتایی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('539', 'شادی ندارد آنکه ندارد به دل غمی', 'آن را که نیست عالم غم، نیست عالمی', 'همایی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('550', 'از هوس ها دور باش و از خدا غافل مباش ', 'زانكه نبود جز خدا اندر دو گیتی یاوری', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('551', 'افتادگی آموز اگر طالب فیضی', ' هرگز نخورد آب، زمینی كه بلند است', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('552', 'اگر شاهی، گدایی، آخرش مرگ', 'اگر زرین كلاهی، آخرش مرگ', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('553', 'ای عشق! چه دردی تو؟ كه درمانت نیست', 'ای جان! به چه زنده ای؟ كه جانانت نیست', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('554', 'آه از این قوم ریایی که در این شهر دو روی', ' روزها شحنه و شب باده فروشند همه', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('555', 'ثمری گر ندهد آه فغان خواهد داد', 'اثری گر نکند ناله دعا خواهم کرد', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('556', 'ثوابت باشد ای دارای خرمن', 'اگر رحمی کنی بر خوشه چینی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('557', 'جان ها در اصل خود عیسی دمند', 'یک دمش زخمند و دیگر مرهمند', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('558', 'چشم دل باز کن تا جان بینی', 'آنچه نادیدنی است آن بینی', 'هاتف اصفهانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('559', 'دانی که چرا سر نهان با تو نگویم؟', 'طوطی صفتی طاقت اسرار نداری', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('560', 'دلی كز معرفت نور و صفا دید', 'به هر چیزی كه دید اول خدا دید', 'شیخ محمود شبستری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('561', 'عیبی نباشد از تو که بر ما جفا رود', 'مجنون از آستانه ی لیلی کجا رود؟', 'سعدی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('562', 'فرا موشم مکن چون من فراموشت نخواهم کرد', 'تو در من آتشی هستی که خاموشت نخواهم کرد', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('563', 'گر نهال شتاب، بنشانی', 'ندهد میوه جز پشیمانی', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('564', 'لازمه عاشقیست رفتن و دیدن ز دور', 'ور نه ز نزدیک هم رخصت دیدار هست', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('565', 'لذت اندر ترک لذت بود، ای آزادگان', 'ما گدایان ترک این لذت نمی دانسته ایم', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('566', 'لطف حق با تو مدارا ها کند', 'چون که از حد بگذزد رسوا کند', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('567', 'مردم از فتنه گریزند و ندانند که ما', 'به تمنای تو در حسرت رستاخیزیم', 'سعدی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('568', 'می رود عمر عزیز ما، دریغا چاره چیست', 'دی برفت و میرود امروز و فردا، چاره چیست', 'شاه نعمت الله ولی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('569', 'یک نعره مستانه ز جایی نشنیدیم', 'ویران شود این شهر که میخوانه ندارد', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('570', 'یاد وصال می کنم، دیده پر آب می شود', 'شرح فراق می کنم، سینه کباب می شود', 'ناشناس', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('572', 'عشق از ره تکلیف به دل پـــا نگذارد', 'سیلاب نپرسد که در خانه کدام است', 'صائب تبریزی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('573', 'نمی خواهم که در چشمم نشینی', 'که آنجا هم میان مردمان است', 'محمود قاجار', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('574', 'تو هم در آینه حیران حسن خویشتنی', 'زمانه‌ایست که هر کس به خود گرفتار است', 'آصفی هروی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('575', 'مردم ز رشک، چند ببینم که جام می', 'لب بر لبت گذارد و قالب تهی کند؟', 'طالب آملی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('577', 'افتادن و برخاستن باده پرستان', 'در مذهب رندان خرابات نماز است', 'غنی کشمیری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('579', 'سبحه بر کف، توبه بر لب، دل پر از ذوق گناه', 'معصیت را خنده می آید ز استغفار ما', 'قدسی مشهدی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('581', 'گریزد از صف ما هر که مرد غوغا نیست', 'کسی که کشته نشد از قبیلۀ ما نیست', 'نظیری نیشابوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('582', 'رفت حاجی به طواف حرم و باز آمد', 'ما به قربان تو رفتیم و همان جا ماندیم', 'میرنجات اصفهانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('583', 'اگر دلدار بی مهر است من هم غیرتی دارم', 'گر او رفت از نظر من نیز خواهم رفت از یادش', 'شاپور تهرانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('584', 'با پستی همت چه زنی دم ز «اناالحق»؟', 'این حرف بلندی ست که بر دار توان زد', 'ظفرخان احسن', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('585', 'یک چند چو ما سلسله جنبان جنون باش', 'تا چند به تقلید خردمند توان بود!؟', 'ظفرخان احسن', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('586', 'در حیرتم که دشمنی کفر و دین چراست', 'از یک چراغ کعبه و بتخانه روشن است', 'ظفرخان احسن', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('587', 'به نام خداوند جان و خرد', 'کزین برتر اندیشه برنگذرد', 'فردوسی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('588', 'شادزی با سیاه چشمان شاد', 'که جهان نیست جز فسانه و باد', 'رودکی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('589', 'به روز نیک کسان گفت تا تو غم نخوری', 'بسا کسا که به روز تو آرزومند است', 'رودکی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('590', 'به دشمن برت استواری مباد', 'که دشمن درختیست تلخ از نهاد', 'بوشکور بلخی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('591', 'بدان کوش که زود دانا شوی', 'چو دانا شدی زود والا شوی', 'بوشکور بلخی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('592', 'شرف و قیمت و قدر تو به فضل و هنر است', 'نه به دیدار و به دینار و به سود و به زیان', 'فرخی سیستانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('593', 'شیر هم شیر بود گرچه به زنجیر بود', 'نبَرَد بند و قلاده شرف شیر ژیان ', 'فرخی سیستانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('594', 'عجب مدار که نامرد مردی آموزد', 'از آن خجسته رسوم و از آن ستوده سِیَر', 'عنصری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('595', 'با دشمن و با دوست بدت می گویم', 'تا هیچ کست دوست ندارد جز من', 'عنصری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('596', 'ای دل ترا بگفتم کز عاشقی حذر کن', 'بگذار نیکوانرا وز مهرشان گذر کن', 'قطران تبریزی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('597', 'نوبهار است در آن کوش که خوشدل باشی', 'که بسی گل بدمد باز و تو در گِل باشی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('598', 'گر چه راهیست پر از بیم ز ما تا بر دوست', 'رفتن آسان بود ار واقف منزل باشی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('599', 'درخت تو گر بار دانش بگیرد', 'به زیر آوری چرخ نیلوفری را', 'ناصر خسرو', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('600', 'نالم به دل چو نای من اندر حصار نای', 'پستی گرفت همت من زین بلند جای', 'ناصر خسرو', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('601', 'هر کاسۀ می که در کف مخموریست', 'از عارض مستی و لب مستوریست', 'خیام', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('602', 'از جملۀ رفتگان این راه دراز', 'باز آمده کیست تا بما گوید راز', 'خیام', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('603', 'شاگردی روزگار کردم بسیار', 'در کار جهان هنوز استاد نیم', 'خیام', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('604', 'محراب جهان جمال رخسارۀ تست', 'سلطان فلک اسیر و بیجاره تست', 'سنایی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('605', 'وقت آنست که مستان طرب از سر گیرند', 'تاج زرین مه از تارک شب برگیرند', 'مجیر', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('606', 'غم کشت مرا و غمگسار آگه نیستض', 'دل خون شد و دلدار آگه نیست', 'ظهیر فاریابی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('607', 'این با که توان گفت که عمرم بگذشت', 'در حسرت روی یار و یار اگه نیست', 'ظهیر فاریابی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('608', 'بسی رنج بردم در این سال و سی', 'عجم زنده کردم بدین پارسی', 'فردوسی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('609', 'حسن تو دایم بدین قرار نماند', 'مست تو جاوید در خمار نماند', 'سعدی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('610', 'وقت را غنیمت دان آن قدر که بتوانی', 'حاصل از حیات ای جان این دم است تا دانی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('611', 'زاهد پشیمان را ذوق باده خواهد کشت', 'عاقلا مکن کاری کاورد پشیمانی', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('612', 'ای ساربان آهسته ران کارام جانم می رود', 'وان دل که با خود داشتم با دلستانم می رود', 'سعدی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('613', 'محمل بدار ای ساروان تندی مکن با کاروان', 'کز عشق آن سرو روان گویی روانم می‌رود', 'سعدی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('614', 'بازآی و بر چشمم نشین ای دلستان نازنین', 'کآشوب و فریاد از زمین بر آسمانم می‌رود', 'سعدی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('615', 'به مژگان سیه کردی هزاران رخنه در دینم', 'بیا کز چشم بیمارت هزاران درد برچینم', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('616', 'جهان پیر است و بی‌بنیاد از این فرهادکش فریاد', 'که کرد افسون و نیرنگش ملول از جان شیرینم', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('617', 'جهان فانی و باقی فدای شاهد و ساقی', 'که سلطانی عالم را طفیل عشق می‌بینم', 'حافظ', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('618', 'حدیث آرزومندی که در این نامه ثبت افتاد', 'همانا بی‌غلط باشد که حافظ داد تلقینم', 'حافظ', '0');
