INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9283', 'چون توانم شد از اینجا که غمش موی کشان', 'دلم آورد و به زنجیر فرو بست اینجا', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9284', 'تا نگوئی که من اینجا ز چه مست افتادم', 'هیچ هشیار نیامد که نشد مست اینجا', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9285', 'کیست این فتنهٔ نوخاسته کز مهر رخش', 'این دل شیفته حال آمد و بنشست اینجا', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9286', 'دل مسکین مرا نیست در اینجا قدری', 'زانک صد دل چو دل خسته من هست اینجا', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9287', 'دوش کز ساغر دل خون جگر میخوردم', 'شیشه نا گه بشد از دستم و بشکست اینجا', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9288', 'گر راه بود بر سر کوی تو صبا را', 'در بندگیت عرضه کند قصه ما را', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9289', 'ما را به سرا پردهٔ قربت که دهد راه', 'برصدر سلاطین نتوان یافت گدا را', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9290', 'گر ره بدواخانهٔ مقصود نیابیم', 'در رنج بمیریم و نخواهیم دوا را', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9291', 'فریاد که دستم نگرفتند و به یکبار', 'از پای فکندند من بی سر و پا را', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9292', 'ساقیا وقت صبوح آمد بیار آن جام را', 'می پرستانیم در ده بادهٔ گلفام را', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9293', 'احتراز از عشق میکردم ولی بیحاصلست', 'هر که از اول تصور میکند فرجام را', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9294', 'ای ترک آتش رخ بیار آن آب آتش فام را', 'وین جامهٔ نیلی ز من بستان و در ده جام را', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9295', 'در حلقهٔ دردی کشان بخرام و گیسو برفشان', 'در حلقهٔ زنجیر بین شیران خون‌آشام را', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9296', 'یک راه در دیر مغان برقع براندازی صنم', 'تا کافران از بتکده بیرون برند اصنام را', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9297', 'اگر سرم برود در سر وفای شما', 'ز سر برون نرود هرگزم هوای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9298', 'بخاک پای شما کان زمان که خاک شوم', 'هنوز بر نکنم دل ز خاک پای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9299', 'چو مرغ جان من از آشیان هوا گیرد', 'کند نزول بخاک در سرای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9300', 'در آن زمان که روند از قفای تابوتم', 'بود مرا دل سرگشته در قفای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9301', 'شوم نشانهٔ تیر قضا بدان اومید', 'که جان ببازم و حاصل کنم رضای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9302', 'کرا بجای شما در جهان توانم دید', 'چرا که نیست مرا هیچکس بجای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9303', 'ز بندگی شما صد هزارم آزادیست', 'که سلطنت کند آنکو بود گدای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9304', 'گرم دعای شما ورد جان بود چه عجب', 'که هست روز و شب اوراد من دعای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9305', 'کجا سزای شما خدمتی توانم کرد', 'جز اینکه روی نپیچم ز ناسزای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9306', 'غریب نیست اگر شد ز خویش بیگانه', 'هر آن غریب که گشستست آشنای شما', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9307', 'ای جان من به یاد لبت تشنه بر شراب', 'هر دم بجام لعل لبت تشنه‌تر شراب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9308', 'در ده قدح که مردم چشمم نشسته است', 'در آرزوی نرگس مست تو در شراب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9309', 'ما را ز جام باده لعلت گریز نیست', 'آری مراد مست نباشد مگر شراب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9310', 'در گردش آرم جام طرب تا مرا دمی', 'از گردش زمانه کند بیخبر شراب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9311', 'بازا بغربت از می و مستی که نزد عقل', 'بر خستگان غریب بود در سفر شراب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9312', 'هر که در عهد ازل مست شد از جام شراب', 'سر ببالین ابد باز نهد مست و خراب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9313', 'بیدلان را رخ زیبا ننمائی به چه وجه', 'عاشقانرا ز در خویش برانی ز چه باب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9314', 'دل ما بی لب لعل تو ندارد ذوقی', 'همه دانند که باشد ز نمک ذوق کباب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9315', 'هر که درآتش سودای تو امروز بسوخت', 'ظاهر آنست که فردا بود ایمن ز عذاب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9316', 'گر چه نقش تو خیالیست که نتوان دیدن', 'همه شب چشم توام مست نمایند بخواب', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9317', 'تا کی ندهی داد من ای داد ز دستت', 'رحم آر که خون در دلم افتاد ز دستت', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9318', 'از دست تو فردا بروم داد بخواهم', 'تا چند کشم محنت و بیداد ز دستت', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9319', 'تا چند کنم ناله و فریاد که در شهر', 'فریاد رسی نیست که فریاد ز دستت', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9320', 'هر چند که سر در سر دستان تو کردیم', 'با این همه دستان نتوان داد ز دستت', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9321', 'از خاک سر کوی تو چون دور فتادم', 'دادیم دل سوخته بر باد ز دستت', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9322', 'از سر جان درگذر گر وصل جانان بایدت', 'بر در دل خیمه زن گر عالم جان بایدت', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9323', 'راه تاریکی نشاید قطع کردن بی‌دلیل', 'خضر راهی برگزین گر آب حیوان بایدت', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9324', 'دل بناکامی بنه گر کام جانت آرزوست', 'ترک مستوری بده گر عیش مستان بایدت', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9325', 'منزلگه جانست که جانان من آنجاست', 'یا روضهٔ خلدست که رضوان من آنجاست', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9326', 'پر می‌زند از شوق لبش طوطی جانم', 'آری چه کنم چون شکرستان من آنجاست', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9327', 'بستان دگر امروز بهشتست ولیکن', 'هرجا که توئی گلشن و بستان من آنجاست', 'خواجوی کرمانی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9328', 'امشب من و تو هردو، مستیم، ز می اما', 'تو مست می حسنی، من، مست می سودا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9329', 'از صحبت من با تو، برخاست بسی فتنه', 'دیوانه چو بنشیند، با مست بود غوغا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9330', 'تا چند زنم حلقه در خانه به غیر از تو', 'چون نیست کسی دیگر، برخیز و درم بگشا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9331', 'در رهگذر مسجد، از مصطبه بگذشتم', 'رندی به کفم برزد دامن که مرو ز اینجا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9332', 'ز شراب لعل نوشین من رند بی نوا را ', 'مددی که چشم مستت به خمار کشت ما را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9333', 'ز وجود خود ملولم قدحی بیار ساقی ', 'برهان مرا زمانی ز خودی خود خدا را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9334', 'بخدا که خون رز را به دو عالم ار فروشیم', 'بخریم هر دو عالم بدهیم خونبها را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9335', 'دل من به یارب آمد ز شکنج بند زلفت', 'مشکن که در دل شب اثری بود دعا را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9336', 'مگس‌وار از سر خوان وصال خود مران ما را', 'نه مهمان توام آخر بخوان روزی بخوان ما را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9337', 'کنار از ما چه می‌جویی میان بگشا دمی، بنشین', 'به اقبالت مگر کاری برآید زین میان ما را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9338', 'نشان آب حیوان کز دهان خضر می‌جستم', 'دهانت می‌دهد اینک به زیر لب نشان ما را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9339', 'نظری نیست، به حال منت ای ماه، چرا؟', 'سایه برداشت ز من مهر تو ناگاه چرا؟', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9340', 'روشن است این که مرا، آینه عمر، تویی', 'در تو آهم نکند، هیچ اثر، آه چرا؟', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9341', 'گر منم دور ز روی تو، دل من با توست', 'نیستی هیچ، ز حال دلم آگاه چرا؟', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9342', 'پادشاه منی و من، ز گدایان توام', 'از گدایان، خبری نیستت ای ماه چرا؟', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9343', 'نقش است هر ساعت ز نو، این دور لعبت باز را', 'ای لعبت ساقی! بیار، آن جام خم پرداز را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9344', 'چون تلخ و شوری می‌چشم، باری بده تا در کشم', 'آن جام نوش انجام را، وان تلخ شور آغاز را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9345', 'ای روشنی بصر! چشم از تو دارم یک نظر', 'بی آنکه یابد زان خبر، آن غمزه غماز را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9346', 'ناز و حفاظ دوستان، حیف آیدم، بر دشمنان', 'ایشان چه می‌دانند قدر این نعمت و این ناز را', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9347', 'ای که روی تو، بهشت دل و جان است مرا!', 'ای که وصل تو مراد دل و جان است، مرا!', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9348', 'چون مراد دل و جانم، تویی از هر دو جهان', 'از تو دل برنکنم، تا دل و جان است مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9349', 'می‌برم نام تو و از تو نشان می‌جویم', 'در ره عشق تو تا، نام و نشان است مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9350', 'دم ز مهر تو زنم، تا ز حیاتم باقی است', 'وصف حسن تو کنم، تا که زبان است مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9351', 'من نه آنم که بخود، از تو بگردانم روی', 'می‌کشم جور تو تا، تاب و توان است مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9352', 'گرچه از چشم نهانی تو، خیال رخ تو', 'روز و شب، مونس و پیدا و نهان است مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9353', 'تو ز من فارغ و آسوده و هر شب تا روز', 'بر سر کوی تو، فریاد و فغان است مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9354', 'ز درد عشق، دل و دیده خون گرفت مرا', 'سپاه عشق، درون و برون گرفت مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9355', 'گرفت دامن من اشک و بر درش بنشاند', 'کجا روم ز درد او که خون گرفت مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9356', 'به سر همی رودم دود و من نمی‌دانم', 'چه آتش است که در اندرون گرفت مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9357', 'زبانه می‌زند، آتش درون من زبان', 'از آنکه دوست به غایت، زبون گرفت مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9358', 'ز بند زلف تو زد، بر دماغ من بویی', 'نسیم صبح ز سودا، جنون گرفت مرا', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9359', 'ره، خرابات است و درد سالخورده، پیر ما ', 'کس نمی‌داند به غیر از پیر ما، تدبیر ما ', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9360', 'خاک را از خاصیت اکسیر اگر، زر می‌کند ', 'ساقیا می‌ده، که ما، خاکیم و می، اکسیر ما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9361', 'ما که از دوران ازل مستیم و عاشق، تا کنون ', 'غالبا صورت نبندد، بعد از این تغییر ما ', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9362', 'من غلام هندوی آن سرو آزادم که او ', 'بر سمن بنوشت خطی، از پی تحریر ما ', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9363', 'بر شب زلفش گر ای باد صبا، یابی گذر ', 'گو حذر کن، زینهار، از ناله شبگیر ما ', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9364', 'ما به سوز آتش دل عالمی می‌سوختیم ', 'گر نه آب چشم ما می‌بود، دامنگیر ما ', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9365', 'ای که می‌گویی مشو دیوانه زلفش بگو ', 'تا نجنباند نسیم صبحدم، زنجیر ما ', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9366', 'خدمتی لایق نمی‌آید ز ما، در خدمتت ', 'وای بر ما، چون نبخشایی تو بر تقصیر ما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9367', 'قبله ما نیست، جز محراب ابروی شما', 'دولت ما نیست، الا در سر کوی شما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9368', 'روز محشر، در جواب پرسش سودای کفر', 'هیچ دست آویز ما را نیست، جز موی شما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9369', 'ماه تابان را شبی نسبت، به رویت، کرده‌ام', 'سالها شد، تا خجالت دارم، از روی شما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9370', 'بی‌گل رویت ندارد، رونقی بستان ما', 'بی حضورت، هیچ نوری نیست، در ایوان ما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9371', 'گر بسامان سر کویش رسی ای باد صبح', 'عرضه داری شرح حال بی سر و سامان ما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9372', 'در دل ما، خار غم بشکست و در دل غم، بماند', 'چیست یاران، چاره غمهای بی‌پایان ما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9373', 'دوستان، گویند دل را صبر فرمایید صبر', 'چون کنیم ای دوستان، دل نیست در فرمان ما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9374', 'در فراق دوست، دل، خون گشت و خواهد شد بباد', 'دوستان بهر خدا جان شما و جان ما', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9375', 'نوبهار و عشق و مستی، خاصه در عهد شباب', 'می‌کند، بنیاد مستوری مستوران، خراب', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9376', 'غنچه مستور صاحبدل، نمی‌بینی که چون', 'بشنود، بوی بهار، از پیش بردارد نقاب', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9377', 'بوی عشرت در بهار، از لاله می‌آید که اوست', 'در دلش، سودای عشق و در سرش جام شراب', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9378', 'دور باد، از نرگس صاحب نظر چشم بدان', 'کو چو چشمت، بر نمی‌دارد سر از مستی و خواب', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9379', 'مدعی منعم مکن، در عاشقی، زیرا که نیست', 'عقل را با پیچ و تاب زلف خوبان، هیچ تاب', 'سلمان ساوجی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9380', 'بیا ای جان بیا ای جان بیا فریاد رس ما را', 'چو ما را یک نفس باشد نباشی یک نفس ما را', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9381', 'جرمی ندارم بیش از این کز جان وفادارم ترا', 'ور قصد آزارم کنی هرگز نیازارم ترا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9382', 'زین جور بر جانم کنون، دست از جفا شستی به خون', 'جانا چه خواهد شد فزون، آخر ز آزارم ترا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9383', 'رخ گر به خون شویم همی، آب از جگر جویم همی', 'در حال خود گویم همی، یادی بود کارم ترا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9384', 'آب رخان من مبر، دل رفت و جان را درنگر', 'تیمار کار من بخور، کز جان خریدارم ترا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9385', 'هان ای صنم خواری مکن، ما را فرازاری مکن', 'آبم به تاتاری مکن، تا دردسر نارم ترا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9386', 'ای کرده در جهان غم عشقت سمر مرا', 'وی کرده دست عشق تو زیر و زبر مرا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9387', 'از پای تا به سر همه عشقت شدم چنانک', 'در زیر پای عشق تو گم گشت سر مرا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9388', 'گر بی‌تو خواب و خورد نباشد مرا رواست', 'خود بی‌تو در چه خور بود خواب و خور مرا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9389', 'عمری کمان صبر همی داشتم به زه', 'آخر به تیر غمزه فکندی سپر مرا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9390', 'باری به عمرها خبری یابمی ز تو', 'چون نیست در هوای تو از خود خبر مرا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9391', 'تا بود در عشق آن دلبر گرفتاری مرا', 'کی بود ممکن که باشد خویشتن‌داری مرا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9392', 'سود کی دارد به طراری نمودن زاهدی', 'چون ز من بربود آن دلبر به طراری مرا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9393', 'ساقی عشق بتم در جام امید وصال', 'می گران دادست کارد آن سبکساری مرا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9394', 'زارم اندر کار او وز کار او هر ساعتی', 'کرد باید پیش خلق انکار و بیزاری مرا', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9395', 'جانا به جان رسید ز عشق تو کار ما', 'دردا که نیستت خبر از روزگار ما', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9396', 'بر آسمان رسد ز فراق تو هر شبی', 'فریاد و ناله های دل زار زار ما', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9397', 'دردا و حسرتا که بجز بار غم نماند', 'با ما به یادگاری از آن روزگار ما', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9398', 'بودیم بر کنار ز تیمار روزگار', 'تا داشت روزگار ترا در کنار ما', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9399', 'آن شد که غمگسار غم ما تو بوده‌ای', 'امروز نیست جز غم تو غمگسار ما', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9400', 'در همه عالم وفاداری کجاست', 'غم به خروارست غمخواری کجاست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9401', 'کارم ز غمت به جان رسیدست', 'فریاد بر آسمان رسیدست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9402', 'نتوان گلهٔ تو کرد اگرچه', 'از دل به سر زبان رسیدست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9403', 'در عشق تو بر امید سودی', 'صد بار مرا زیان رسیدست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9404', 'هرجا که رسم برابر من', 'اندوه تو در میان رسیدست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9405', 'مهرت به دل و به جان دریغست', 'عشق تو به این و آن دریغست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9406', 'وصل تو بدان جهان توان یافت', 'کان ملک بدین جهان دریغست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9407', 'قدر چو تویی زمین چه داند', 'کان قدر به آسمان دریغست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9408', 'در کوی وفای تو به انصاف', 'یک دل به هزار جان دریغست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9409', 'هرکه چون من به کفرش ایمانست', 'از همه خلق او مسلمانست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9410', 'روی ایمان ندیده‌ای به خدا', 'گر به ایمان خویشت ایمانست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9411', 'ماه چون چهرهٔ زیبای تو نیست', 'مشک چون زلف گل‌آرای تو نیست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9412', 'کس ندیدست رخ خوب ترا', 'که چو من بنده و مولای تو نیست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9413', 'کردم از دیده و دل جای ترا', 'گرچه از دیده و جان جای تو نیست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9414', 'چه دهی وعدهٔ فردا که مرا', 'دل این وعدهٔ فردای تو نیست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9415', 'سینهٔ کس نشناسم به جهان', 'که در آن سینه تقاضای تو نیست', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9416', 'رایت حسن تو از مه برگذشت', 'با من این جور تو از حد درگذشت', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9417', 'صبر کن ای تن که آن بیداد هجران بگذرد', 'راحت تن چون که بگذشت آفت جان بگذرد', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9418', 'تا در این دوری ز داروی و ز درمان چاره چیست', 'صبر کن چندان که این دوران دونان بگذرد', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9419', 'گرچه مهجورم تن اندر درد هجران کی دهم', 'روزی آخر یاد ما بر یاد جانان بگذرد', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9420', 'گرچه در پیمان تست این دم چنان غافل مباش', 'کین جهان مختصرآباد ویران بگذرد', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9421', 'ماه‌رویا تکیه بر عشق من و خوبی خویش', 'بس مکن زیرا که هم این و هم آن بگذرد', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9422', 'دردم فزود و دست به درمان نمی‌رسد', 'صبرم رسید و هجر به پایان نمی‌رسد', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9423', 'جان داده‌ام مگر که به جانان خود رسم', 'جانم برون شدست و به جانان نمی‌رسد', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9424', 'خوانی که خواجهٔ خرد از بهر جان نهاد', 'مهمان عقل بر سر آن خوان نمی‌رسد', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9425', 'جانا به غریبستان چندین بنماند کس', 'باز آی که در غربت قدر تو نداند کس', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9426', 'صد نامه فرستادم یک نامهٔ تو نامد', 'گویی خبر عاشق هرگز نرساند کس', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9427', 'هر کو ز می وصلت یک جام بیاشامد', 'تا زنده بود او را هشیار نخواند کس', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9428', 'جانا به غریبستان چندین ز چه می‌مانی', 'بازآ تو از این غربت تا چند پریشانی', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9429', 'صد نامه فرستادم صد راه نشان دادم', 'یا راه نمی‌دانی یا نامه نمی‌خوانی', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9430', 'گر نامه نمی‌خوانی خود نامه تو را خواند', 'ور راه نمی‌دانی در پنجه ره دانی', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9431', 'بازآ که در آن محبس قدر تو نداند کس', 'با سنگ دلان منشین چون گوهر این کانی', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9432', 'ای از دل و جان رسته دست از دل و جان شسته', 'از دام جهان جسته بازآ که ز بازانی', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9433', 'هم آبی و هم جویی هم آب همی‌جویی', 'هم شیر و هم آهویی هم بهتر از ایشانی', 'مولانا', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9434', 'بدان عزمم که دیگر ره به میخانه کمر بندم', 'دل اندر وصل و هجر آن بت بیدادگر بندم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9435', 'به رندی سر برافرازم به باده رخ برافروزم', 'ره میخانه برگیرم در طامات بربندم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9436', 'ز خیر و شر چو حاصل شد سر از گردون برآرد خود', 'من نادان چه معنی را دل اندر خیر و شر بندم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9437', 'ترا من دوست می‌دارم ندانم چیست درمانم', 'نه روی هجر می‌بینم نه راه وصل می‌دانم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9438', 'نپرسی هرگز احوالم نسازی چارهٔ کارم', 'نه بگذاری که با هرکس بگویم راز پنهانم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9439', 'دلم بردی و آنگاهی به پندم صبر فرمایی', 'مکن تکلیف ناواجب که بی‌دل صبر نتوانم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9440', 'اگر با من نخواهی ساخت جانم همچو دل بستان', 'که بی‌وصل تو اندر دل وبال دل بود جانم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9441', 'بیا ای راحت جانم که جان را بر تو افشانم', 'زمانی با تو بنشینم ز دل این جوش بنشانم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9442', 'قیامت می‌کنی ای کافر امروز', 'ندانم تا چه داری در سر امروز', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9443', 'تویی سلطان بت‌رویان که در حسن', 'ندارد چون تو سلطان سنجر امروز', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9444', 'به حق آنکه داد ای بت جمالت', 'به حال بنده یک‌دم بنگر امروز', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9445', 'دردا و دریغا که دل از دست بدادم', 'واندر غم و اندیشه و تیمار فتادم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9446', 'آبی که مرا نزد بزرگان جهان بود', 'خوش خوش همه بر باد غم عشق تو دادم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9447', 'با وصل تو نابوده هنوزم سر و کاری', 'سر بر خط بیداد و جفای تو نهادم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9448', 'دل در سخن زرق زراندود تو بستم', 'تا در غم تو خون دل از دیده گشادم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9449', 'مپسند که با خاک برم درد فراقت', 'چون دست غم عشق تو برداد به بادم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9450', 'با آنکه نباشی نفسی جز به خلافم', 'هرگز نفسی جز به رضای تو مبادم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9451', 'آخر در زهد و توبه دربستم', 'وز بند قبول آن و این رستم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9452', 'کار جهان نگر که جفای که می‌کشم', 'دل را به پیش عهد وفای که می‌کشم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9453', 'بهر رضای دوست ز دشمن جفا کشند', 'چون دوست نیست بهر رضای که می‌کشم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9454', 'تو دانی که من جز تو کس را ندانم', 'تویی یار پیدا و یار نهانم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9455', 'مرا جای صبر است و دانم که دانی', 'ترا جای شکرست و دانی که دانم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9456', 'مرا گویی که از من بجز غم نبینی', 'همین است اگر راست خواهی گمانم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9457', 'گر از وصل تو شاد گردم و گرنه', 'به هرسان که باشد ز غم درنمانم', 'انوری', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9458', 'باد نوروز وزیده است به کوه و صحرا', 'جامه عید بپوشند، چه شاه و چه گدا', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9459', 'الا یا ایها الساقی! ز می پُر ساز جامم را', 'که از جانم فرو ریزد، هوای ننگ و نامم را', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9460', 'از آن می ریز در جامم که جانم را فنا سازد', 'برون سازد ز هستی، هسته نیرنگ و دامم را', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9461', 'از آن می ده که جانم را  ز قید خود رها سازد', 'به خود گیرد زمامم را، فرو ریزد مقامم را', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9462', 'از آن می ده که در خلوتگه رندان بیحرمت', 'به هم کوبد سجودم را، به هم ریزد قیامم را', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9463', 'به تو دل بستم و غیر تو کسی نیست مرا', 'جُز تو ای جان جهان، دادرسی نیست مرا', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9464', 'عاشق روی توام، ای گل بی مثل و مثال', 'به خدا، غیر تو هرگز هوسی نیست مرا', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9465', 'با تو هستم، ز تو هرگز نشدم دور؛ ولی', 'چه توان کرد که بانگ جرسی نیست مرا', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9466', 'پرده از روی بینداز، به جان تو قسم', 'غیر دیدار رخت ملتمسی نیست مرا', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9467', 'مده از جنت و از حور و قصورم خبری', 'جز رخ دوست نظر سوی کسی نیست مرا', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9468', 'الا یا ایها الساقی! برون بر حسرت دلها', 'که جامت حل نماید یکسره اسرار مشکلها', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9469', 'به می بر بند راه عقل را از خانقاه دل', 'که این دارالجنون هرگز نباشد جای عاقلها', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9470', 'اگر دل بسته‏ای بر عشق جانان، جای خالی کن', 'که این میخانه هرگز نیست جز ماوای بیدلها', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9471', 'جزعشق تو، هیچ نیست اندر دل ما', 'عشق تو سرشته گشته اندر گلِ ما', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9472', 'اسفار و شفاء ابن سینا نگشود', 'با آن همه جرّ و بحثها مشکل ما', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9473', 'با شیخ بگو که راه من باطل خواند', 'بر حقّ تو لبخند زند باطل ما', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9474', 'گر سالک او منازلی سیر کند', 'خود مسلک نیستی بود منزل ما', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9475', 'صد قافله دل، بار به مقصد بستند', 'بر جای بماند این دل غافل ما', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9476', 'پرده بردار ز رخ، چهره‏گشا ناز بس است', 'عاشق سوخته را دیدن رویت هوس است', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9477', 'دست از دامنت ای دوست، نخواهم برداشت', 'تا من دلشده را یک رمق و یک نفس است', 'امام خمینی', '0');
INSERT INTO "poems" ("ID", "Verse1", "Verse2", "Des1", "Type1") VALUES ('9478', 'مرغ پر سوخته را نیست نصیبی ز بهار', 'عرصه جولانگه زاغ است و نوای مگس است', 'امام خمینی', '0');